    import type { Scout } from "$lib/types.ts";
    export let scouts: Scout[] = [];

    export let access_token: string;

    async function set_admin(scout: Scout, is_admin: boolean) {
        let res = await fetch("https://localhost:3007/admin/set", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "x-access-token": access_token,
            },
            body: JSON.stringify({ scout, is_admin }),
        });
        if (res.ok) {
            console.log("User " + scout.name + " is_admin set to " + is_admin);
            scout.admin = !scout.admin;
        } else {
            console.log("Failed to set user " + scout.name + " permissions");
        }
    }
</script>

